mapper.select:
select goodsCode, barcode, classifyId, pClassifyId, goodsName, goodsImg, goodsUnit, isHot, goodsType
vipPrice, discountsPrice, specialSalePrice, wholesalePrice, price, deleted, createTime,
updateTime, goodsDesc, goodsSpec
from tb_goods
where goodsCode = #{goodscode,jdbcType=BIGINT} or barcode = #{barcode,jdbcType=BIGINT}
 or goodsName = #{goodsname,jdbcType=VARCHAR}

select goodsCode, barcode, classifyId, pClassifyId, goodsName, goodsImg, goodsUnit, isHot, goodsType vipPrice, discountsPrice, specialSalePrice, wholesalePrice, price, deleted, createTime, updateTime, goodsDesc, goodsSpec from tb_goods
(goodsCode like #{keyword} or barcode like #{keyword} or goodsName like #{keyword})
and classifyId like #{classifyid}
and pClassifyId like #{pclassifyid}
order by createTime desc
